import "./App.css";
import { createApi } from "unsplash-js";
import React from "react";

import LazyImage from "./LazyImage/lazy-image.component";

import Masonry from "react-masonry-css";

const browserApi = createApi({
  apiUrl: "https://mywebsite.com/unsplash-proxy"
});

export class App extends React.Component {
  constructor() {
    super();

    this.state = {
      photosList: [],
      page: 1
    };
  }

  async componentDidMount() {
    await this.updatePhotos();
    window.addEventListener("scroll", this.calculateScroll);
  }

  calculateScroll = (e) => {
    if (
      window.scrollY + window.innerHeight >=
      document.body.offsetHeight - document.body.offsetHeight * 0.25
    ) {
      this.setState({ page: this.state.page + 1 });
      this.updatePhotos(20);
    }
  };

  updatePhotos = async (itemsCount) => {
    const unsplash = createApi({
      accessKey: "0ZpmaJxEBHSlQAtQ4_EaJ1wSe1J5S-c1WTEA8duYfOU"
    });
    const { response } = await unsplash.photos.list({
      page: this.state.page,
      perPage: itemsCount || 15
    });
    // const updatedList = this.state.photosList.concat(
    //   response.results.filter((item) => {
    //     if (!item.sponsorship) {
    //       item.loaded = false;
    //       return item;
    //     }
    //     return false;
    //   }) // remove AD pictures
    // );

    this.setState({ photosList: response.results });
  };

  render() {
    return (
      <div className="mt-10 mx-auto" style={{ maxWidth: 1200 }}>
        <Masonry
          breakpointCols={3}
          className="my-masonry-grid flex -ml-5"
          columnClassName="my-masonry-grid_column bg-clip-padding pl-5"
        >
          {this.state.photosList.map((photo) => (
            <LazyImage {...photo} key={photo.id} />
          ))}
        </Masonry>
      </div>
    );
  }
}

export default App;
