import React from "react";

import {
  IoAdd,
  IoAddCircleOutline,
  IoDocumentsOutline,
  IoCodeSlashOutline
} from "react-icons/io5";

const ImageControls = ({ element, cyanHandler }) => (
  <div className="cursor-zoom-in z-20 absolute inset-0 opacity-0 transition-opacity hover:opacity-100 active:cursor-zoom-out">
    {/* <div
      className="inline-block shadow-lg absolute right-3 bottom-3 z-10 bg-white p-2 rounded-full"
      onClick={() => cyanHandler(element)}
    >
      <IoAddCircleOutline
        size={25}
        className="opacity-30 transition-opacity duration-200 hover:opacity-100"
      />
    </div> */}

    <div
      className="cursor-pointer inline-flex items-center justify-center shadow-lg absolute right-3 z-10 bg-white rounded-full"
      style={{ height: 35, width: 35, bottom: "14.5rem" }}
      onClick={() => cyanHandler(element)}
    >
      <IoCodeSlashOutline
        size={22}
        className="opacity-30 transition-opacity duration-200 hover:opacity-100"
      />
    </div>

    <div
      className="cursor-pointer inline-flex items-center justify-center shadow-lg absolute right-3 z-10 bg-white rounded-full"
      style={{ height: 35, width: 35, bottom: "10rem" }}
      onClick={() => cyanHandler(element)}
    >
      <IoCodeSlashOutline
        size={22}
        className="opacity-30 transition-opacity duration-200 hover:opacity-100"
      />
    </div>

    <div
      className="cursor-pointer inline-flex items-center justify-center shadow-lg absolute right-3 z-10 bg-white rounded-full"
      style={{ height: 35, width: 35, bottom: "5.5rem" }}
      onClick={() => cyanHandler(element)}
    >
      <IoCodeSlashOutline
        size={22}
        className="opacity-30 transition-opacity duration-200 hover:opacity-100"
      />
    </div>

    <div
      className="cursor-pointer inline-flex items-center justify-center shadow-lg absolute right-3 z-10 bg-white rounded-full"
      style={{ height: 35, width: 35, bottom: "1rem" }}
      onClick={() => cyanHandler(element)}
    >
      <IoAdd
        size={25}
        className="opacity-30 transition-opacity duration-200 hover:opacity-100"
      />
    </div>
    <div className="absolute inset-0 z-0 h-full w-full bg-black bg-opacity-30"></div>
  </div>
);

export default ImageControls;
