import React from "react";

import { IoLocationOutline } from "react-icons/io5";

const ImageDetailsControls = (props) => {
  function download(size) {
    console.log(size);
  }

  return (
    <div className="mt-3">
      {props.location.title ? (
        <h4 className="flex items-center text-gray-700 mb-2">
          <IoLocationOutline size={20} />
          <span className="text-xl">{props.location.title}</span>
        </h4>
      ) : null}
      <span onClick={download("small")}>download small</span>
    </div>
  );
};
export default ImageDetailsControls;
