import "./App.css";
import { createApi } from "unsplash-js";
import React from "react";

import Image from "./image/image.component";
import { Blurhash } from "react-blurhash";

const browserApi = createApi({
  apiUrl: "https://mywebsite.com/unsplash-proxy"
});

export class App extends React.Component {
  constructor() {
    super();

    this.state = {
      photosList: [],
      page: 1
    };
  }

  async componentDidMount() {
    await this.updatePhotos();
    // window.addEventListener("scroll", this.calculateScroll);
  }

  calculateScroll = (e) => {
    if (
      window.scrollY + window.innerHeight >=
      document.body.offsetHeight - document.body.offsetHeight * 0.2
    ) {
      this.setState({ page: this.state.page + 1 });
      this.updatePhotos(20);
    }
  };

  updatePhotos = async (itemsCount) => {
    console.log("calling");
    const unsplash = createApi({
      accessKey: "0ZpmaJxEBHSlQAtQ4_EaJ1wSe1J5S-c1WTEA8duYfOU"
    });
    const { response } = await unsplash.photos.list({
      page: this.state.page,
      perPage: itemsCount || 5
    });
    const updatedList = this.state.photosList.concat(
      response.results.filter((item) => {
        if (!item.sponsorship) {
          item.loaded = false;
          return item;
        }
        return false;
      }) // remove AD pictures
    );

    this.setState({ photosList: updatedList });
  };

  updateLoadState = (item, loadstate) => {
    const updatedItem = item;
    item.loaded = loadstate;
    // console.log(updatedItem);
    this.setState({ photosList: [...this.state.photosList, updatedItem] });
    // console.log(item, loadstate);
    // console.log(...this.state.photosList);
  };

  render() {
    return (
      <div className="container mx-auto flex flex-wrap justify-around">
        {this.state.photosList.map((photo) => (
          <div key={photo.id} className="m-5 flex items-center justify-center">
            <Blurhash
              hash={photo.blur_hash}
              width={400}
              height={600}
              resolutionX={32}
              resolutionY={32}
              punch={1}
            />
            <img
              src={photo.urls.regular}
              alt={photo.alt_description}
              width={400}
              height={600}
              onLoad={this.updateLoadState(photo, true)}
            />
          </div>
        ))}
      </div>
    );
  }
}

export default App;
