const LazyImage = (props) => (
  <div>
    {props.loaded ? (
      <img
        src={props.urls.regular}
        alt={props.alt_description}
        width={400}
        height={600}
        onLoad={() => {
          console.log(props.id, props.loaded);
          props.loaded = true;
        }}
      />
    ) : (
      <Blurhash
        hash={props.blur_hash}
        width={400}
        height={600}
        resolutionX={32}
        resolutionY={32}
        punch={1}
      />
    )}
  </div>
);

export default LazyImage;
