import React from "react";

import { createApi } from "unsplash-js";

// import LazyImage from "../LazyImage/lazy-image.component";
import LazyImage from "../LazyImage/lazy-image.component";
import Masonry from "react-masonry-css";

export default class ImageContainer extends React.Component {
  constructor() {
    super();

    this.state = {
      photosList: [],
      page: 1,
      loading: false
    };
  }

  async componentDidMount() {
    await this.updatePhotos();
    window.addEventListener("scroll", this.calculateScroll);
  }

  getDocHeight = () => {
    var D = document;
    return Math.max(
      D.body.scrollHeight,
      D.documentElement.scrollHeight,
      D.body.offsetHeight,
      D.documentElement.offsetHeight,
      D.body.clientHeight,
      D.documentElement.clientHeight
    );
  };

  calculateScroll = async (e) => {
    const percentageScrolled = Math.floor(
      (window.scrollY / (this.getDocHeight() - window.innerHeight)) * 100
    );

    if (percentageScrolled > 90 && !this.state.loading) {
      this.setState({ page: this.state.page + 1 });
      this.setState({ loading: true });
      await this.updatePhotos(20);
      this.setState({ loading: false });
    }
  };

  updatePhotos = async (itemsCount) => {
    const unsplash = createApi({
      accessKey: "0ZpmaJxEBHSlQAtQ4_EaJ1wSe1J5S-c1WTEA8duYfOU"
    });
    const { response } = await unsplash.photos.list({
      page: this.state.page,
      perPage: itemsCount || 15
    });
    const updatedList = this.state.photosList.concat(
      response.results.filter((item) => {
        if (!item.sponsorship) {
          item.loaded = false;
          return item;
        }
        return false;
      }) // remove AD pictures
    );

    this.setState({ photosList: updatedList });
  };

  render() {
    return (
      <div className="mt-10 mx-auto" style={{ maxWidth: 1200 }}>
        <Masonry
          breakpointCols={3}
          className="my-masonry-grid flex -ml-5"
          columnClassName="my-masonry-grid_column bg-clip-padding pl-5"
        >
          {this.state.photosList.map((photo) =>
            photo.blur_hash ? <LazyImage {...photo} key={photo.id} /> : null
          )}
        </Masonry>
      </div>
    );
  }
}
