import "./App.css";
import { createApi } from "unsplash-js";
import React from "react";

import LazyImage from "./LazyImage/lazy-image.component";

import Masonry from "react-masonry-css";

const browserApi = createApi({
  apiUrl: "https://mywebsite.com/unsplash-proxy"
});

const masonryOpts = { transitionDuration: 0, columnWidth: 100 };
export class App extends React.Component {
  constructor() {
    super();

    this.state = {
      photosList: [],
      page: 1
    };
  }

  async componentDidMount() {
    await this.updatePhotos();
    // window.addEventListener("scroll", this.calculateScroll);
  }

  calculateScroll = (e) => {
    if (
      window.scrollY + window.innerHeight >=
      document.body.offsetHeight - document.body.offsetHeight * 0.2
    ) {
      this.setState({ page: this.state.page + 1 });
      this.updatePhotos(20);
    }
  };

  updatePhotos = async (itemsCount) => {
    console.log("calling");
    const unsplash = createApi({
      accessKey: "0ZpmaJxEBHSlQAtQ4_EaJ1wSe1J5S-c1WTEA8duYfOU"
    });
    const { response } = await unsplash.photos.list({
      page: this.state.page,
      perPage: itemsCount || 8
    });
    const updatedList = this.state.photosList.concat(
      response.results.filter((item) => {
        console.log(item.sponsorship);
        if (!item.sponsorship) {
          item.loaded = false;
          return item;
        }
        return false;
      }) // remove AD pictures
    );

    this.setState({ photosList: updatedList });
  };

  updateLoadState = (item, loadstate) => {
    console.log(item);
  };

  render() {
    return (
      <div className="container grid mx-auto">
        <Masonry
          breakpointCols={2}
          className="my-masonry-grid flex"
          columnClassName="my-masonry-grid_column bg-clip-padding"
        >
          {this.state.photosList.map((photo) => (
            // <div>
            //   <img
            //     src={photo.urls.regular}
            //     alt={photo.alt_description}
            //     className={`max-width-400`}
            //   />
            // </div>
            <LazyImage {...photo} key={photo.id} />
          ))}
        </Masonry>
      </div>
    );
  }
}

export default App;
