import React from "react";

import { IoLocationOutline } from "react-icons/io5";

const ImageDetailsControls = (props) => {
  async function download(size) {
    const a = document.createElement("a");
    a.href = await toDataURL(props.urls[size]);
    a.download = "";
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  }

  async function toDataURL(url) {
    return fetch(url)
      .then((response) => {
        return response.blob();
      })
      .then((blob) => {
        return URL.createObjectURL(blob);
      });
  }

  return (
    <div className="mt-3">
      {props.location.title ? (
        <h4 className="flex items-center text-gray-700 mb-2">
          <IoLocationOutline size={20} />
          <span className="text-xl">{props.location.title}</span>
        </h4>
      ) : null}
      <button type="submit" onClick={() => download("small")}>
        Download!
      </button>
      {/* <span onClick={() => download("small")}>download small</span> */}
    </div>
  );
};
export default ImageDetailsControls;
