import React from "react";

const ImageControls = ({ cyanHandler }) => (
  <div className="z-20 absolute inset-0 opacity-0 transition-opacity hover:opacity-100">
    <div
      className="relative z-10"
      style={{ width: 100, height: 100, background: "red" }}
    />
    <div
      className="relative z-10"
      style={{ width: 100, height: 100, background: "cyan" }}
      onClick={cyanHandler}
    />
    <div
      className="relative z-10"
      style={{ width: 100, height: 100, background: "blue" }}
    />
    <div className="absolute inset-0 z-0 h-full w-full bg-black bg-opacity-30"></div>
  </div>
);

export default ImageControls;
