import "./App.css";
import { createApi } from "unsplash-js";
import React from "react";

const browserApi = createApi({
  apiUrl: "https://mywebsite.com/unsplash-proxy"
});

export class App extends React.Component {
  constructor() {
    super();

    this.state = {
      photosList: []
    };
  }

  async componentDidMount() {
    const unsplash = createApi({
      accessKey: "0ZpmaJxEBHSlQAtQ4_EaJ1wSe1J5S-c1WTEA8duYfOU"
    });
    const { response } = await unsplash.photos.list({ perPage: 50 });

    this.setState({ photosList: response.results });

    window.addEventListener("scroll", this.calculateScroll);
  }

  calculateScroll = (e) => {
    console.log(window.scrollY);
    if (window.scrollY + window.innerHeight >= document.body.offsetHeight) {
      console.log("end");
    }
  };

  render() {
    return (
      <div className="container mx-auto flex flex-wrap justify-around">
        {this.state.photosList.map((photo) => (
          <div key={photo.id} className="m-5 flex items-center justify-center">
            <img src={photo.urls.thumb} alt={photo.alt_description} />
          </div>
        ))}
      </div>
    );
  }
}

export default App;
