export default class LazyImage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoaded: false
    };
  }

  render() {
    return (
      <div>
        <img
          src={this.props.urls.regular}
          alt={this.props.alt_description}
          width={400}
          height={600}
          className={this.props.loaded ? "opacity-100" : "opacity-0"}
        />
        <Blurhash
          hash={this.props.blur_hash}
          width={400}
          height={600}
          resolutionX={32}
          resolutionY={32}
          punch={1}
        />
      </div>
    );
  }
}

