import React from "react";

const AuthorBio = (props) => (
  <div className="flex mb-2">
    <div className="rounded-full overflow-hidden" style={{ height: 45, width: 45 }}>
      <img src={props.profile_image.small} alt="" />
    </div>
    <div className="ml-2">
      <h2 className="text-3xl">
        <span>{props.first_name}</span>
        {props.last_name ? <span>{props.last_name}</span> : null}
      </h2>
      {props.bio ? <h3 className="text-xl">{props.bio}</h3> : null}
    </div>
  </div>
);

export default AuthorBio;
