import React from "react";

const AuthorDetails = (props) => (
  <div>
    <h2>
      <span>{props.first_name}</span>
      {props.last_name ? <span>{props.last_name}</span> : null}
    </h2>
    {props.bio ? <h3>{props.bio}</h3> : null}

  </div>
);

export default AuthorDetails;
