import React from "react";

import { IoLocationOutline, IoLogoInstagram } from "react-icons/io5";

const AuthorContacts = (props) => (
  <div className="mt-3 flex flex-col">
    {props.location ? (
      <h4 className="flex items-center text-gray-700">
        <IoLocationOutline size={20} />
        <span className="text-xl">{props.location}</span>
      </h4>
    ) : null}
    {props.instagram_username ? (
      <a
        href={`https://www.instagram.com/${props.instagram_username}`}
        className="flex items-center text-gray-700 text-xl hover:underline"
      >
        <IoLogoInstagram size={20} />
        <span className="ml-1">{props.instagram_username}</span>
      </a>
    ) : null}
  </div>
);

export default AuthorContacts;
