import React from "react";

import { IoLocationOutline } from "react-icons/io5";
import { toDataURL } from "../../utils/index";

import Btn from "../Btn/btn.component";

const ImageDetailsControls = (props) => {
  async function download(size) {
    const a = document.createElement("a");
    a.href = await toDataURL(props.urls[size]);
    a.download = "";
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  }

  return (
    <div className="mt-3">
      <Btn contentClasses="mr-5" onClick={() => download("small")}>Small</Btn>
      <Btn contentClasses="mr-5" onClick={() => download("regular")}>Medium</Btn>
      <Btn onClick={() => download("full")}>Large</Btn>
      {/* <span onClick={() => download("small")}>download small</span> */}
    </div>
  );
};
export default ImageDetailsControls;
