import React from "react";
import { Link } from "react-router-dom";

import {
  IoAdd,
  IoAddCircleOutline,
  IoDocumentsOutline,
  IoCodeSlashOutline
} from "react-icons/io5";

const ImageControls = ({ element, cyanHandler, copyHandler }) => (
  <div className="p-5 cursor-zoom-in z-20 absolute inset-0 opacity-0 transition-opacity hover:opacity-100 active:cursor-zoom-out">
    <a
      href={element.links.html}
      target="_blank"
      rel="noreferrer"
      className="relative z-10 block text-white font-normal text-2xl truncate transition-all opacity-80 hover:opacity-100"
    >
      {element.alt_description.charAt(0).toUpperCase() +
        element.alt_description.slice(1)}
    </a>

    <div
      className="cursor-pointer inline-flex items-center justify-center shadow-lg absolute right-3 z-10 bg-white rounded-full"
      style={{ height: 35, width: 35, bottom: "5.5rem" }}
      onClick={() => copyHandler(element)}
    >
      <IoCodeSlashOutline
        size={22}
        className="opacity-30 transition-opacity duration-200 hover:opacity-100"
      />
    </div>

    <div
      className="cursor-pointer inline-flex items-center justify-center shadow-lg absolute right-3 z-10 bg-white rounded-full"
      style={{ height: 35, width: 35, bottom: "1rem" }}
      onClick={() => cyanHandler(element)}
    >
      <IoAdd
        size={25}
        className="opacity-30 transition-opacity duration-200 hover:opacity-100"
      />
    </div>
    <div className="absolute inset-0 z-0 h-full w-full bg-black bg-opacity-20"></div>
  </div>
);

export default ImageControls;
