import React from "react";
import { Blurhash } from "react-blurhash";

export default class LazyImage extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      isLoaded: false
    };
  }

  render() {
    return (
      <div>
        <img
          src={this.props.urls.regular}
          alt={this.props.alt_description}
          width={400}
          height={600}
          className={this.state.isLoaded ? "opacity-100" : "opacity-0"}
          onLoad={() => {
            this.setState({ isLoaded: true });
          }}
        />
        <Blurhash
          hash={this.props.blur_hash}
          width={400}
          height={600}
          resolutionX={32}
          resolutionY={32}
          punch={1}
          className={this.state.isLoaded ? "opacity-0" : "opacity-100"}
        />
      </div>
    );
  }
}
