const LazyImage = (props) => (
  <div>
    <img
      src={props.urls.regular}
      alt={props.alt_description}
      width={400}
      height={600}
      className={props.loaded ? "opacity-100" : "opacity-0"}
    />
    <Blurhash
      hash={props.blur_hash}
      width={400}
      height={600}
      resolutionX={32}
      resolutionY={32}
      punch={1}
    />
  </div>
);

export default LazyImage;
